import { Component } from '@angular/core';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent {
  navigation_active: string = '#';
  setnavigation_active(value: string) {
    this.navigation_active = value;
  }
}
<nav>
  <div class="iconBar">
    <a routerLink="" (click)="setnavigation_active('#')" [ngClass]="navigation_active === '#' ? 'active' : ''"><i class="fa fa-home"></i></a>
    <a routerLink="aboutme" (click)="setnavigation_active('#about')" [ngClass]="navigation_active === '#about' ? 'active' : ''"><i class="fa-regular fa-user"></i></a>
    <a routerLink="mywork" (click)="setnavigation_active('#work')" [ngClass]="navigation_active === '#work' ? 'active' : ''"><i class="fa-solid fa-briefcase"></i></a>
    <a routerLink="hobbies" (click)="setnavigation_active('#hobbies')" [ngClass]="navigation_active === '#hobbies' ? 'active' : ''"><i class="fa-regular fa-face-smile"></i></a>
  </div>
</nav>
